<?php

class M_butir extends CI_Model {

        public function __construct()
        {
                // Call the CI_Model constructor
                parent::__construct();
                $this->load->database();
                $this->load->helper('date');
        }

        public function get_entire_data()
        {
                //$query = $this->db->get('butir');

                return $this->db->select("*")
              ->from('butir')
              ->order_by('butir', 'ASC')
              ->get()
              ->result_array();
			  /*
			  $this->order_by('butir','ASC');
			  $query = $this->get('butir');
			  return $query->result_array();
			  */
        }

        public function insert_entry()
        {
                $data = array(
                        'butir' => $this->input->post('butir'),
                        'bakumutu' => $this->input->post('bakumutu'),
                        // 'penjelasan' => $this->input->post('penjelasan'),
                        'keterangan' => $this->input->post('keterangan'),
                        'id_borang' => $this->input->post('id_borang'),
                        'created_at'=> date('Y-m-d H:i:s'),
                );

                $this->db->insert('butir', $data);
                $data = array(
                        'user'=> $_SESSION['name'],
                        'action' => "Menambahkan Butir  ".$this->input->post('butir'),
                        'created_at'=> date('Y-m-d H:i:s')
                );

                $this->db->insert('log', $data);
        }

        public function insert_entry_buku()
        {
                $data = array(
                        'butir' => $this->input->post('butir'),
                        'bakumutu' => $this->input->post('bakumutu'),
                        // 'penjelasan' => $this->input->post('penjelasan'),
                        'keterangan' => $this->input->post('keterangan'),
                        'id_borang' => $this->input->post('id_borang'),
                        'created_at'=> date('Y-m-d H:i:s'),
                );

                $this->db->insert('butir_buku', $data);
                $data = array(
                        'user'=> $_SESSION['name'],
                        'action' => "Menambahkan Butir  ".$this->input->post('butir'),
                        'created_at'=> date('Y-m-d H:i:s')
                );

                $this->db->insert('log', $data);
        }

        public function update_butir_buku_penjelasan()
        {
                $data = array(
                        'penjelasan' => $this->input->post('penjelasan'),
                        'updated_at'=> date('Y-m-d H:i:s'),
                );

                $this->db->update('butir_buku', $data, array('id' => $_POST['id_butir']));
                $data = array(
                        'user'=> $_SESSION['name'],
                        'action' => "Menambahkan Penjelasan dari id butir :  ".$this->input->post('id_butir'),
                        'created_at'=> date('Y-m-d H:i:s')
                );

                $this->db->insert('log', $data);
        }

        public function buatbutir3AS1($id){
